export const useRegroupImageAndArticle = (
  data: IArticle | ITopicResponse | MyArticle | undefined | null
) => {
  const result = useMemo(() => {
    if (data === undefined || data === null) {
      return '';
    }

    return regroupArticle(data);
  }, [data]);

  return {
    result,
  };
};
